package com.ecyrd.jspwiki.ui.stripes;

public enum NodeType
{
    /** Root node */
    ROOT(null, null),
    /** Attribute node */
    ATTRIBUTE("",""),
    /** Attribute node generated by an embedded JSP tag. */
    DYNAMIC_ATTRIBUTE("<",">"),
    /** Text node */
    TEXT("", ""),
    /** CDATA node. */
    CDATA("<![CDATA[","]]>"),
    /** DOCTYPE declaration */
    DOCTYPE("<!DOCTYPE ", ">"),
    /** HTML comment tag */
    HTML_COMMENT("<!--", "-->"),
    /** HTML start tag */
    HTML_START_TAG("<", ">"),
    /** HTML LINK tag. */
    HTML_LINK("<",">"),
    /** HTML META tag. */
    HTML_META("<",">"),
    /** HTML end tag */
    HTML_END_TAG("</", ">"),
    /** HTML end tag */
    HTML_COMBINED_TAG("<", "/>"),
    /** HTML tag, but not sure whether it's a start, end or combined tag. */
    UNRESOLVED_HTML_TAG("<", null),
    /** JSP comments, e.g., &lt;%-- comment --%&gt; */
    JSP_COMMENT("<%--", "--%>"),
    /**
     * JSP declaration, e.g., &lt;%! declaration; [ declaration; ]+ ...
     * %&gt;
     */
    JSP_DECLARATION("<%!", "%>"),
    /** JSP expression, e.g., &lt;%= expression %&gt; */
    JSP_EXPRESSION("<%=", "%>"),
    /**
     * JSP scriptlet, e.g., &lt;% code fragment %&gt;. Note the whitespace
     * after the %.
     */
    SCRIPTLET("<%", "%>"),
    /**
     * JSP page, import or taglib directive, e.g., &lt;%@ include... %&gt;
     * &lt;%@ page... %&gt; &lt;%@ taglib... %&gt;
     */
    JSP_DIRECTIVE("<%@ ", "%>");
    private final String m_tagStart;

    private final String m_tagEnd;

    NodeType(String tagStart, String tagEnd )
    {
        m_tagStart = tagStart;
        m_tagEnd = tagEnd;
    }

    public String getTagEnd()
    {
        return m_tagEnd;
    }

    public String getTagStart()
    {
        return m_tagStart;
    }
    
    public String toString() {
        return name();
    }
    
}